$(document).ready(function () {
	$("#userForm").submit(function (event) {
		event.preventDefault();
		var userForm = {};
		userForm["name"] = $("#name").val();
		userForm["surname"] = $("#surname").val();

		$.ajax({
			type: "POST",
			contentType: "application/json",
			url: "/user",
			data: JSON.stringify(userForm),
			dataType: "json",
			cache: false,
			timeout: 600000,
			success: function (data) {
				var json = "Ajax respons" + JSON.stringify(data, null, 4);
				$("#feedback").html(json);
			},
			error: function (e) {
				var json = "Ajax Error" + e.responseText;
				$("#feedback").html(json);
			}
		});
	});
});